        var imageNumb = 0;

        self.percentageSign = ko.observable(false);
        self.view2 = ko.observable(false);
        self.sliderView = ko.observable(viewNumb);
        self.imageView = ko.observable(imageNumb);
        self.percentage = ko.observable("0");
