                                $('.regionalizationAutocomplete li:last-child a').on('click', function(event) {
                                    limitExists=true;
                                    $('.regionalizationAutocomplete').hide();
                                    $regionalizePLZ.autocomplete("search", self.request().term);
                                    event.stopImmediatePropagation();                   
                                });
